import React, { useState } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

import "./horizontal.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  indexAxis: "y",
  elements: {
    bar: {
      borderWidth: 2,
    },
  },
  responsive: true,
  plugins: {
    legend: {
      position: "right",
    },
    title: {
      display: true,
      text: "Chart.js Horizontal Bar Chart",
    },
  },
};

const HorizontalBarChart = () => {
  const [state,setState] = useState({
    positive:13451,
    negatives:11839
  })
  const data = {
    labels: ["Positive", "Negative", "Comments"],
    datasets: [
      {
        label: "'# of Reviews'",
        data: [13451, 11839, 10463],
        borderColor: "rgb(53, 162, 235)",
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };
  return (
    <>
      <div className="bar">
        <Bar data={data} options={options} />
      </div>
      <div className="bar-hover">
        <ul>
            <li><a>Total Reviews</a></li>
            <li><a>Positive Reviews</a></li>
            <li><a>Negative Reviews</a></li>
            <li><a>Comments</a></li>
        </ul>
      </div>
    </>
  );
};

export default HorizontalBarChart;
