import React, { useState } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

import "./horizontal.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  indexAxis: "y",
  elements: {
    bar: {
      borderWidth: 2,
    },
  },
  responsive: true,
  plugins: {
    legend: {
      position: "right",
    },
    title: {
      display: true,
      text: "Chart.js Horizontal Bar Chart",
    },
  },
};

const HorizontalBarChart = () => {
  const [state,setState] = useState({
    positive:13451,
    negatives:11839,
    comments:10463
  })
  const {positive,negatives,comments} = state

  const data = {
    labels: ["Positive", "Negative", "Comments"],
    datasets: [
      {
        label: "'# of Reviews'",
        data: [positive, negatives, comments],
        borderColor: "rgb(53, 162, 235)",
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };

  const changeData = (val) =>{
        console.log(val)
  }
  return (
    <>
      <div className="bar">
        <Bar data={data} options={options} />
      </div>
      <div className="bar-hover">
        <h3 onMouseEnter={()=>changeData('tol')}>Total Reviews</h3>
        <h3 onMouseEnter={()=>changeData('tol')}>Positives</h3>
        <h3 onMouseEnter={()=>changeData('tol')}>Negatives</h3>
        <h3>Comments</h3>
      </div>
    </>
  );
};

export default HorizontalBarChart;
