import React, { useState } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

import "./horizontal.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  indexAxis: "y",
  elements: {
    bar: {
      borderWidth: 2,
    },
  },
  responsive: true,
  plugins: {
    legend: {
      position: "right",
    },
    title: {
      display: true,
      text: "Chart.js Horizontal Bar Chart",
    },
  },
};

const HorizontalBarChart = () => {
  const [state,setState] = useState({
    positive:13451,
    negatives:11839,
    comments:10463
  })
  const {positive,negatives,comments} = state

  const data = {
    labels: ["Positive", "Negative", "Comments"],
    datasets: [
      {
        label: "'# of Reviews'",
        data: [positive, negatives, comments],
        borderColor: "rgb(53, 162, 235)",
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };

  const changeData = (val) =>{

  }
  return (
    <>
      <div className="bar">
        <Bar data={data} options={options} />
      </div>
      <div className="bar-hover">
        <ul>
            <li><p onClick={changeData('tol')}>Total Reviews</p></li>
            <li><p onClick={changeData('pos')}>Positive Reviews</p></li>
            <li><a href='javascript:void(0)' onClick={changeData('neg')}>Negative Reviews</a></li>
            <li><a href='javascript:void(0)' onClick={changeData('tol')}>Comments</a></li>
        </ul>
      </div>
    </>
  );
};

export default HorizontalBarChart;
